# test_settings.py - Test-specific Django settings\nfrom .crm_project.settings import *\n\n# Override settings for testing\nDATABASES = {\n    'default': {\n        'ENGINE': 'django.db.backends.sqlite3',\n        'NAME': ':memory:',\n    }\n}\n\n# Disable migrations for faster testing\nclass DisableMigrations:\n    def __contains__(self, item):\n        return True\n    \n    def __getitem__(self, item):\n        return None\n\nMIGRATION_MODULES = DisableMigrations()\n\n# Test-specific settings\nPASSWORD_HASHERS = [\n    'django.contrib.auth.hashers.MD5PasswordHasher',  # Faster for tests\n]\n\n# Disable logging during tests\nLOGGING = {\n    'version': 1,\n    'disable_existing_loggers': True,\n    'handlers': {\n        'null': {\n            'class': 'logging.NullHandler',\n        },\n    },\n    'root': {\n        'handlers': ['null'],\n    },\n}\n\n# Test communication settings\nWHATSAPP_API_URL = 'https://test.whatsapp.api'\nWHATSAPP_ACCESS_TOKEN = 'test-token'\nWHATSAPP_PHONE_NUMBER_ID = 'test-phone-id'\n\nWECHAT_CORP_ID = 'test-corp-id'\nWECHAT_CORP_SECRET = 'test-corp-secret'\nWECHAT_AGENT_ID = 'test-agent-id'\n\n# Test UAT settings\nENABLE_PUBLIC_UAT_VIEWS = True\nUAT_ACCESS_TOKEN = 'test-uat-token'\n\n# Disable CSRF for API tests\nREST_FRAMEWORK['DEFAULT_AUTHENTICATION_CLASSES'] = [\n    'rest_framework.authentication.TokenAuthentication',\n]\n\n# Disable throttling for tests\nREST_FRAMEWORK['DEFAULT_THROTTLE_CLASSES'] = []\nREST_FRAMEWORK['DEFAULT_THROTTLE_RATES'] = {}\n\n# Use console email backend for tests\nEMAIL_BACKEND = 'django.core.mail.backends.console.EmailBackend'