#!/usr/bin/env python\n\"\"\"Test runner for the CRM application\"\"\"\nimport os\nimport sys\nimport django\nfrom django.conf import settings\nfrom django.test.utils import get_runner\n\nif __name__ == \"__main__\":\n    os.environ.setdefault('DJANGO_SETTINGS_MODULE', 'crm_project.test_settings')\n    django.setup()\n    TestRunner = get_runner(settings)\n    test_runner = TestRunner()\n    \n    # Run specific app tests\n    failures = test_runner.run_tests([\"crm\"])\n    \n    if failures:\n        sys.exit(1)